// let's imagine this file is autogenerated from the backend
// ideally, we want to keep these api related types in sync
// with the backend instead of manually writing them out

export type BaseEntity = {
  id: string;
  createdAt: number;
};

export type Entity<T> = {
  [K in keyof T]: T[K];
} & BaseEntity;

export type Meta = {
  page: number;
  total: number;
  totalPages: number;
};

export type Pagination = {
  total: number;
  page: number;
  limit: number;
  nextUrl: string;
};

export type User = Entity<{
  username: string;
  email: string;
  password: string;
}>;

export type AuthResponse = {
  jwt: string;
  user: User;
};

/*==========Testing zone==========*/
export type UserResponse = {
  succcess: boolean;
  message: string;
  data: {
    id: string;
    username: string;
    email: string;
    karma: number;
    bio: string;
    avatar: string;
    role: string;
    created_at: string;
    updated_at: string;
  };
};

export type RegisterResponse = {
  success: boolean;
  message: string;
};

export type LoginResponse = {
  success: boolean;
  message: string;
  data: {
    username: string;
    avatar: string;
    access_token: string;
  };
};

export type Community = {
  id: string;
  name: string;
  shortDescription: string;
  isPrivate: boolean;
  totalMembers: number;
};
export type CommunityResponse = {
  success: boolean;
  message: string;
  data: Community[];
};
/*==========End Testing zone==========*/

export type Team = Entity<{
  name: string;
  description: string;
}>;

export type Discussion = Entity<{
  title: string;
  body: string;
  teamId: string;
  author: User;
}>;

export type Comment = Entity<{
  body: string;
  discussionId: string;
  author: User;
}>;
