export type User = {
  id: number;
  username: string;
  email: string;
  bio: string;
  avatar: string;
  achievement: {
    karma: number;
    badge: string;
    totalPosts: number;
    totalComments: number;
  };
  createdAt: string;
};

export type RegisterResponse = {
  success: boolean;
  message: string;
};

type ModeratedCommunity = {
  communityId: number;
  role: string;
};
export type LoginResponse = {
  success: boolean;
  message: string;
  data: {
    username: string;
    avatar: string;
    accessToken: string;
    moderatedCommunities: ModeratedCommunity[];
  };
};

/* ____________________Community____________________ */
type Moderators = {
  userId: number;
  username: string;
  avatar: string;
  role: string;
};
export type Community = {
  id: number;
  name: string;
  shortDescription: string;
  description: string;
  topic: string[];
  communityAvatar: string;
  coverImage: string;
  isPrivate: boolean;
  createdAt: string;
  totalMembers: number;
  moderators: Moderators[];
};
/* ____________________End Community____________________ */

export type CommunityResponse = {
  success: boolean;
  message: string;
  data: Community[];
};

export type CreateCommunityResponse = {
  success: boolean;
  message: string;
};

/* ____________________Post____________________ */
export type Post = {
  id: number;
  communityId: number;
  community: {
    id: number;
    name: string;
  };
  authorId: number;
  author: {
    id: number;
    username: string;
    avatar: string;
  };
  title: string;
  type: string;
  content: string;
  mediaUrls: string[];
  tags: string[];
  vote: number;
  createdAt: string;
  updatedAt: string;
};
/* ____________________Comment____________________ */
export type Comment = {
  id: number;
  postId: number;
  author: {
    id: number;
    username: string;
    avatar: string;
  };
  parentCommentId?: number;
  content: string;
  mediaUrl: string;
  vote: number;
  createdAt: string;
  updatedAt: string;
  replies: Comment[];
};

/* ____________________Image Response____________________ */

export type UploadImageResponse = {
  success: boolean;
  message: string;
  data: {
    url: string;
  }[];
};

/* ____________________Messages____________________ */
export type Message = {
  id: number;
  conversationId: number;
  sender: {
    id: number;
    username: string;
    avatar: string;
  };
  type: string;
  content: string;
  isRead: boolean;
  readAt: string | null;
  createdAt: string;
  attachments: string[];
};

export type Conversation = {
  id: number;
  otherUser: {
    id: number;
    username: string;
    avatar: string;
  };
  lastMessage: {
    content: string;
    isRead: boolean;
    sentAt: string;
    readAt: string | null;
  } | null;
  unreadCount: number;
  createdAt: string;
  updatedAt: string;
};

export type ConversationsResponse = {
  success: boolean;
  message: string;
  data: Conversation[];
  pagination: Pagination;
};

export type MessagesResponse = {
  success: boolean;
  message: string;
  data: Message[];
  pagination: Pagination;
};

export type SendMessageResponse = {
  success: boolean;
  message: string;
  data: Message & {
    conversationId: number;
  };
};

// SSE Event types
export type SSEMessageEvent = {
  conversationId: number;
  message: Message;
};

export type SSEConversationUpdatedEvent = {
  conversation: Conversation;
};
/* ____________________End Messages____________________ */

